<?php/** * This is the model class for table "pin_master". * * The followings are the available columns in table 'pin_master': * @property integer $id * @property string $created_date * @property integer $created_by * @property integer $transfer_to * @property integer $no_pin * @property integer $pkg_id * @property integer $pin_pkg_amt * @property integer $flag */class Pinmaster extends CActiveRecord{	/**	 * @return string the associated database table name	 */	public function tableName()	{		return 'pin_master';	}	/**	 * @return array validation rules for model attributes.	 */	public function rules()	{		// NOTE: you should only define rules for those attributes that		// will receive user inputs.		return array(			array('no_pin, pkg_id', 'required'),			//array('id, created_by, transfer_to, no_pin, pkg_id, pin_pkg_amt, flag', 'numerical', 'integerOnly'=>true),			// The following rule is used by search().			// @todo Please remove those attributes that should not be searched.			//array('id, created_date, created_by, transfer_to, no_pin, pkg_id, pin_pkg_amt, flag', 'safe', 'on'=>'search'),		);	}	/**	 * @return array relational rules.	 */	public function relations()	{		// NOTE: you may need to adjust the relation name and the related		// class name for the relations automatically generated below.		return array(		);	}	/**	 * @return array customized attribute labels (name=>label)	 */	public function attributeLabels()	{		return array(			'id' => 'ID',			'created_date' => 'Created Date',			'created_by' => 'Created By',			'transfer_to' => 'Transfer To (Sponsor)',			'no_pin' => 'Number of Pin',			'pkg_id' => 'Package',			'pin_pkg_amt' => 'Pin Pkg Amt',			'flag' => 'created/Transfer',		);	}	/**	 * Retrieves a list of models based on the current search/filter conditions.	 *	 * Typical usecase:	 * - Initialize the model fields with values from filter form.	 * - Execute this method to get CActiveDataProvider instance which will filter	 * models according to data in model fields.	 * - Pass data provider to CGridView, CListView or any similar widget.	 *	 * @return CActiveDataProvider the data provider that can return the models	 * based on the search/filter conditions.	 */	public function search()	{		// @todo Please modify the following code to remove attributes that should not be searched.		$criteria=new CDbCriteria;		$criteria->compare('id',$this->id);		$criteria->compare('created_date',$this->created_date,true);		$criteria->compare('created_by',$this->created_by);		$criteria->compare('transfer_to',$this->transfer_to);		$criteria->compare('no_pin',$this->no_pin);		$criteria->compare('pkg_id',$this->pkg_id);		$criteria->compare('pin_pkg_amt',$this->pin_pkg_amt);		$criteria->compare('flag',$this->flag);		return new CActiveDataProvider($this, array(			'criteria'=>$criteria,		));	}	/**	 * Returns the static model of the specified AR class.	 * Please note that you should have this exact method in all your CActiveRecord descendants!	 * @param string $className active record class name.	 * @return Pinmaster the static model class	 */	public static function model($className=__CLASS__)	{		return parent::model($className);	}}